                          par$cod_pais                => par$cod_pais,
                          par$oid_cuenta              => par$oid_cuenta_comision,
                          par$oid_sector              => par$oid_sector_boveda);

        END IF;

      ELSE
        /* 2040150009 - El DeviceID informado esta relacionado a una planificacin que no controla facturacin y por esta razn el movimiento no podr ser generado. */
        dbms_output.put_line('El DeviceID informado esta relacionado a una planificacin que no controla facturacin y por esta razn el movimiento no podr ser generado.');
