    /* SUBCLIENTE */
    var$aoid_sc_cliente                  gepr_pcomon_###VERSION###.tipo$oids_;
    var$anel_sc_cli_index                gepr_pcomon_###VERSION###.tipo$nels_;
    var$anel_sc_index                    gepr_pcomon_###VERSION###.tipo$nels_;
