                                      'par$saida_oid_calidad: ' || var$aux_oid_calidad(var$aux_oid_calidad.COUNT -1) || const$new_line ||
                                      'par$saida_num_importe: ' || var$aux_num_importe(var$aux_num_importe.COUNT -1) || const$new_line ||
