                  END LOOP;
                END IF;

                SAPR_PLOG_API.SAGREGA_DETALLE(par$oid_llamada  => par$oid_llamada,
                                      par$des_origen      => const$des_origen,
                                      par$des_version     => const$version,                            
                                      par$des_detalle     => 'Previo a cargar los valores',
