                                                                      || const$new_line || 'par$oid_tipo_subcliente: '  || par$aoid_sc_tipo_subcliente(idxSci)
                                                                      || const$new_line || 'par$bol_enviado_saldos: '   || par$abol_sc_enviado_saldos(idxSci)
