    var$mensajePlan              gepr_pcomon_###VERSION###.tipo$desc_;
    var$sinReintentos            gepr_pcomon_###VERSION###.tipo$nel_ := 0;

   
  BEGIN
    -- INICIO - LOG de parametros iniciales
